import React, { Fragment, useState, useEffect } from 'react';
import { useStore } from '../../store/fechaCotizacionStore';

const FechasCotizacion = () => {
  const { fecha_doc, fecha_vencimiento, setFechas } = useStore();

  const [fechaActual, setFechaActual] = useState('');

  useEffect(() => {
    const today = new Date();
    const day = String(today.getDate()).padStart(2, '0');
    const month = String(today.getMonth() + 1).padStart(2, '0');
    const year = today.getFullYear();
    const formattedDate = `${day}/${month}/${year}`;
    setFechaActual(formattedDate);
  }, []);

  const handleFechaChange = (event) => {
    const { name, value } = event.target;
    setFechas({ ...useStore.getState(), [name]: value });
  };

  const fechaVencimiento = () => {
    const fechaActualParts = fechaActual.split('/');
    const day = parseInt(fechaActualParts[0], 10);
    const month = parseInt(fechaActualParts[1], 10);
    const year = parseInt(fechaActualParts[2], 10);
    const fechaVencimientoDate = new Date(year, month - 1, day + 3);
    const dayVencimiento = String(fechaVencimientoDate.getDate()).padStart(2, '0');
    const monthVencimiento = String(fechaVencimientoDate.getMonth() + 1).padStart(2, '0');
    const yearVencimiento = fechaVencimientoDate.getFullYear();
    const formattedFechaVencimiento = `${dayVencimiento}/${monthVencimiento}/${yearVencimiento}`;
    return formattedFechaVencimiento;
  };

  const formatDate = (date) => {
    const dateObj = new Date(date);
    const day = String(dateObj.getDate()).padStart(2, '0');
    const month = String(dateObj.getMonth() + 1).padStart(2, '0');
    const year = dateObj.getFullYear();
    return `${day}/${month}/${year}`;
  };

  return (
    <Fragment>
      <div className="w-4/12 h-50">
        <div className="bg-white rounded-lg shadow-lg p-4">
          <h2 className="text-lg font-bold mb-4">Fechas:</h2>
          <form>
            <div className="mb-4">
              <input
                type="date"
                id="fecha"
                name="fecha_doc"
                className="border rounded px-4 py-2 w-full"
                value={fecha_doc || fechaActual}
                onChange={handleFechaChange}
              />
            </div>
            <div>
              <label htmlFor="fecha-vencimiento" className="block text-gray-700 font-bold mt-8 mb-2">
                Fecha de vencimiento:
              </label>
              <input
                type="date"
                id="fecha-vencimiento"
                name="fecha_vencimiento"
                className="border rounded px-4 py-2 w-full"
                value={fecha_vencimiento ? formatDate(fecha_vencimiento) : fechaVencimiento()}
                onChange={handleFechaChange}
              />
            </div>
          </form>
        </div>
      </div>
    </Fragment>
  );
};

export default FechasCotizacion;
