import React, { Fragment, useState } from 'react';
import { useStore } from '../../store/fechaCotizacionStore';

const FechasCotizacion = () => {
  const { fecha_doc, fecha_vencimiento, setFechas } = useStore();

  const [fechaActual, setFechaActual] = useState('');

  // Obtener fecha actual al cargar el componente
  useState(() => {
    const today = new Date();
    const formattedDate = today.toISOString().split('T')[0];
    setFechaActual(formattedDate);
  }, []);

  // Manejar el cambio de fecha
  const handleFechaChange = (event) => {
    const { name, value } = event.target;
    setFechas({ ...useStore.getState(), [name]: value });
  };

  // Calcular fecha de vencimiento
  const fechaVencimiento = () => {
    const fechaActualDate = new Date(fechaActual);
    const fechaVencimientoDate = new Date(fechaActualDate);
    fechaVencimientoDate.setDate(fechaVencimientoDate.getDate() + 3);
    const formattedFechaVencimiento = fechaVencimientoDate.toISOString().split('T')[0];
    return formattedFechaVencimiento;
  };

  return (
    <Fragment>
      <div className="w-4/12 h-50">
        <div className="bg-white rounded-lg shadow-lg p-4">
          <h2 className="text-lg font-bold mb-4">Fechas:</h2>
          <form>
            <div className="mb-4">
              <input
                type="date"
                id="fecha"
                name="fecha_doc"
                className="border rounded px-4 py-2 w-full"
                value={fecha_doc || fechaActual}
                onChange={handleFechaChange}
              />
            </div>
            <div>
              <label htmlFor="fecha-vencimiento" className="block text-gray-700 font-bold mt-8 mb-2">
                Fecha de vencimiento:
              </label>
              <input
                type="date"
                id="fecha-vencimiento"
                name="fecha_vencimiento"
                className="border rounded px-4 py-2 w-full"
                value={fecha_vencimiento || fechaVencimiento()}
                onChange={handleFechaChange}
              />
            </div>
          </form>
        </div>
      </div>
    </Fragment>
  );
};

export default FechasCotizacion;
