import React, { Fragment, useState } from 'react';

const ClientesIndex = () => {
  const [nombre, setNombre] = useState("");
  const [contacto1, setContacto1] = useState("");
  const [contacto2, setContacto2] = useState("");
  const [condicionesPago, setCondicionesPago] = useState("");
  const [rfc, setRfc] = useState("");
  const [moneda, setMoneda] = useState("");
  const [vendedor, setVendedor] = useState("");

  const handleArchivoChange = (event) => {
    const archivoSeleccionado = event.target.files[0];
    // Aquí puedes manejar el archivo seleccionado, por ejemplo, subirlo a un servidor o almacenarlo en el estado del componente.
  };


  const guardarDatos = () => {
    // Aquí puedes implementar la lógica para guardar los datos del formulario
    console.log("Datos guardados:", {
      nombre,
      contacto1,
      contacto2,
      condicionesPago,
      rfc,
      moneda,
      vendedor,
    });
  };

  return (
    <Fragment>
      <div className="flex items-center justify-center h-screen m-20">
        <div className="w-8/12 bg-white rounded-lg shadow-lg p-4">
          <h2 className="text-lg font-bold mb-4">Nuevo Cliente</h2>
          <div className="mb-4">
            <label className="block font-bold">Nombre</label>
            <input
              type="text"
              className="border rounded px-4 py-2 w-full"
              value={nombre}
              onChange={(e) => setNombre(e.target.value)}
            />
          </div>
          <div className="mb-4">
            <label className="block font-bold">Telefono</label>
            <input
              type="tel"
              className="border rounded px-4 py-2 w-full"
              value={contacto1}
              onChange={(e) => setContacto1(e.target.value)}
            />
          </div>
          <div className="mb-4">
            <label className="block font-bold">E-mail</label>
            <input
              type="email"
              className="border rounded px-4 py-2 w-full"
              value={contacto2}
              onChange={(e) => setContacto2(e.target.value)}
            />
          </div>
          <div className="mb-4">
            <label className="block font-bold">Condiciones de Pago</label>
            <select
              className="border rounded px-4 py-2 w-full"
              value={condicionesPago}
              onChange={(e) => setCondicionesPago(e.target.value)}
            >
              <option value="">Seleccione una opción</option>
              {/* Aquí puedes agregar las opciones del select */}
            </select>
          </div>
          <div className="mb-4">
            <label className="block font-bold">RFC</label>
            <input
              type="text"
              className="border rounded px-4 py-2 w-full"
              value={rfc}
              onChange={(e) => setRfc(e.target.value)}
            />
          </div>
          <div className="mb-4">
            <label className="block font-bold">Moneda</label>
            <select
              className="border rounded px-4 py-2 w-full"
              value={moneda}
              onChange={(e) => setMoneda(e.target.value)}
            >
              <option value="">Seleccione una opción</option>
              {/* Aquí puedes agregar las opciones del select */}
            </select>
          </div>
          <div className="mb-4">
            <label className="block font-bold">Vendedor</label>
            <select
              className="border rounded px-4 py-2 w-full"
              value={vendedor}
              onChange={(e) => setVendedor(e.target.value)}
            >
              <option value="">Seleccione una opción</option>
              {/* Aquí puedes agregar las opciones del select */}
            </select>
          </div>

          <div className="mb-4">
            <label className="block font-bold">Subir Constancia de Situación Fiscal (opcional)</label>
            <input
              type="file"
              accept=".pdf"
              className="border rounded px-4 py-2 w-full"
              onChange={handleArchivoChange}
            />
          </div>

          <div className="flex justify-end">
            <button
              type="button"
              className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded mr-2"
              onClick={guardarDatos}
            >
              Guardar
            </button>
            <button
              type="button"
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
            >
              Salir
            </button>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default ClientesIndex;
