import React, { Fragment } from 'react';
import { useArticulosStore } from '../../store/articulosStore';

const Desgloce = () => {

    const DesgloceTotales = useArticulosStore((state) => state.listaArticulos);

    // Calcular subtotal
    const subtotal = DesgloceTotales.reduce(
        (total, articulo) => total + articulo.PRECIO,
        0
    );

    // Calcular IVA
    const iva = DesgloceTotales.reduce(
        (total, articulo) => total + articulo.IMPUESTOS,
        0
    );

    // Calcular total
    const total = subtotal + iva;

    return (
        <Fragment>
            <div className="mt-1 mx-3 bg-white rounded-lg shadow-lg p-4 w-1/4">
                <div className="text-gray-600 text-sm font-light mb-4">
                    <div className="flex justify-between items-center">
                        <span className="text-xl">Subtotal</span>
                        <span className="text-right text-xl">${subtotal}</span>
                    </div>
                    <div className="flex justify-between items-center">
                        <span className="text-left text-xl">IVA 16%</span>
                        <span className="text-right text-xl">${iva}</span>
                    </div>
                    <div className="flex justify-between items-center font-bold">
                        <span className="text-left text-xl">Total</span>
                        <span className="text-right text-xl">${total}</span>
                    </div>
                </div>
            </div>
        </Fragment>
    );
};

export default Desgloce;
