import React, { Fragment, useState, useEffect } from 'react';
import { useStore } from '../../store/fechaCotizacionStore';

const FechasCotizacion = () => {
  const { fecha_doc, fecha_vencimiento, setFechas } = useStore();

  const [fechaActual, setFechaActual] = useState('');
  const [fechaVencimiento, setFechaVencimiento] = useState('');

  useEffect(() => {
    const today = new Date();
    const day = String(today.getDate()).padStart(2, '0');
    const month = String(today.getMonth() + 1).padStart(2, '0');
    const year = today.getFullYear();
    const formattedDate = `${day}/${month}/${year}`;
    setFechaActual(formattedDate);

    const vencimientoDate = new Date(today.getTime() + 3 * 24 * 60 * 60 * 1000);
    const vencimientoDay = String(vencimientoDate.getDate()).padStart(2, '0');
    const vencimientoMonth = String(vencimientoDate.getMonth() + 1).padStart(2, '0');
    const vencimientoYear = vencimientoDate.getFullYear();
    const formattedVencimiento = `${vencimientoDay}/${vencimientoMonth}/${vencimientoYear}`;
    setFechaVencimiento(formattedVencimiento);
  }, []);

  const handleFechaChange = (event) => {
    const { name, value } = event.target;
    setFechas({ ...useStore.getState(), [name]: value });
  };

  return (
    <Fragment>
      <div className="w-4/12 h-50">
        <div className="bg-white rounded-lg shadow-lg p-4">
          <h2 className="text-lg font-bold mb-4">Fechas:</h2>
          <form>
            <div className="mb-4">
              <input
                type="dat"
                id="fecha"
                name="fecha_doc"
                className="border rounded px-4 py-2 w-full"
                value={fecha_doc || fechaActual}
                onChange={handleFechaChange}
              />
            </div>
            <div>
              <label htmlFor="fecha-vencimiento" className="block text-gray-700 font-bold mt-8 mb-2">
                Fecha de vencimiento:
              </label>
              <input
                type="text"
                id="fecha-vencimiento"
                name="fecha_vencimiento"
                className="border rounded px-4 py-2 w-full"
                value={fecha_vencimiento || fechaVencimiento}
                onChange={handleFechaChange}
              />
            </div>
          </form>
        </div>
      </div>
    </Fragment>
  );
};

export default FechasCotizacion;
