import { create } from 'zustand';

const useTotalesStore = create((set) => ({
  subtotal: 0,
  impuestosTotal: 0,
  totalGeneral: 0,
  setSubtotal: (value) => set({ subtotal: value }),
  setImpuestosTotal: (value) => set({ impuestosTotal: value }),
  setTotalGeneral: (value) => set({ totalGeneral: value }),
}));

export default useTotalesStore;
