import React, { Fragment } from 'react';

//importamos los componentes de este index
import BuscadorClientes from './BuscadorClientes';
import FechasCotizacion from './FechasCotizacion';
import TablaProductos from './TablaProductos';

const CotizacionesIndex = () => {
  return (
    <Fragment>
      <div className="flex justify-between m-5 h-full">
        {/*importamos el componente de buscador de clientes aquí*/}
        <BuscadorClientes />
        {/*importamos el componente que asigna las fechas de la cotización aquí*/}
        <FechasCotizacion />
      </div>
      <div className="flex flex-row">
        {/*importamos el componente de la tabla de productos aquí*/}
        <TablaProductos></TablaProductos>
        <div class="mt-1 mx-3 bg-white rounded-lg shadow-lg p-4 w-1/4">
          <div class="text-gray-600 text-sm font-light mb-4">
            <div class="flex justify-between items-center">
              <span class="text-xl">Subtotal</span>
              <span class="text-right text-xl">$145</span>
            </div>
            <div class="flex justify-between items-center">
              <span class="text-left text-xl">IVA 16%</span>
              <span class="text-right text-xl">$23.20</span>
            </div>
            <div class="flex justify-between items-center font-bold">
              <span class="text-left text-xl">Total</span>
              <span class="text-right text-xl">$168.20</span>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default CotizacionesIndex;
